/**
* 获取图片的类型
* @param file
* @return
*/
public static String getImageMimeType(String file) {
BitmapFactory.Options options = new BitmapFactory.Options();
options.inJustDecodeBounds = true;
BitmapFactory.decodeFile(file, options);
String type = options.outMimeType;
if (!TextUtils.isEmpty(type)) {
if (type.length() &gt; 6) {
type = type.substring(6, type.length());
}
return type;
} else {
return null;
}
}

function validEmail(email) {
if(email.length &gt; 0) {
var reg = /^([a-zA-Z0-9]+[_|\_|\.]?)*[a-zA-Z0-9]+@([a-zA-Z0-9]+[_|\_|\.]?)*[a-zA-Z0-9]+\.[a-zA-Z]{1,10}$/;
if(!reg.test(email)) {
return false;
}
} else {
return false;
}
return true;
}
function validPhone(phone) {
var isPhone = /^([0-9]{3,4}-?)?[0-9]{7,8}$/;
var isMob=/^((\+?[0-9]{2})|(\(\+?[0-9]{2}\)))?(13[012356789][0-9]{8}|15[012356789][0-9]{8}|18[02356789][0-9]{8}|147[0-9]{8}|1349[0-9]{7})$/;
if(isMob.test(phone)||isPhone.test(phone)) {
return true;
} else {
return false;
}
}

function validQQ(qq) {

return /[0-9]{5,13}/.test(qq);

}
